class Solution {
public:
    int maxOperations(string s) {
        int res = 0;
        int cou1 = 0;
        bool has_same = false;
        for (int i = 0; i < s.size(); i++) {
            if (s[i] == '1') {
                cou1++;
                has_same = false;
            } else if (s[i] == '0') {
                if (!has_same) {
                    res += cou1;
                    has_same = true;
                }
            }
        }
        return res;
    }
};
