class Solution {
public:
    int maxOperations(string shabd) {
        int parinaam = 0;
        int eka = 0;
        bool sthiti = false;

        for (int i = 0; i < shabd.size(); i++) {
            processChar(shabd[i], eka, sthiti, parinaam);
        }

        return parinaam;
    }

private:
    void processChar(char ch, int &eka, bool &sthiti, int &parinaam) {
        if (ch == '1') {
            eka++;
            sthiti = true;
        } else {
            if (sthiti) {
                parinaam += eka;
            }
            sthiti = false;
        }
    }

};
