class Solution {
public:
    int maxOperations(string s) {
    int a = 0, b = 0;
    bool c = true;
    for (int i = 0; i < s.size(); i++) {
        if (s[i] == '1') {
            b++;
            c = true;
        } else {
            if (c) a += b;
            c = false;
        }
    }
    return a;
}
};
