#include<bits/stdc++.h>
#define LL long long
#define len(G) (LL)(G).size()
#define all(G) (G).begin(),(G).end()
using namespace std;
using i128=__int128;
using ar=array<LL,2>;
using Poly=vector<LL>;

class Solution {
public:
    int maxOperations(string s) {
        LL n=len(s);
        for(LL i=0;i<n;++i)
            s[i]-='0';
        s+=2;
        LL ans=0,cnt=0;
        for(LL i=n-1;i>=0;--i)
        {
            if(s[i])
            {
                if(s[i+1]==0)
                {
                    ++cnt;
                }
                ans+=cnt;
            }
        }
        return ans;
    }
};
