class Solution {
public:
    int maxOperations(string s) {
        ios_base::sync_with_stdio(false);
    cin.tie(NULL);
        int ans = 0, o = 0;
        bool b = true;
        int i = 0;
        while (i < s.size()) {
            s[i] == '1' ? (o++, b = true) : (b ? ans += o : 0, b = false);
            i++;
        }
        return ans;
    }

