class Solution {
public:
    int maxOperations(string s) {
        int ans = 0;
        int n = s.size();
        int cto = 0, last = -1;
        for(int i = 0 ; i<n ; i++){
            if(s[i] == '1'){
                if(last == -1){
                    last = i;
                    cto++;
                    continue;
                }
                if(i - last == 1){
                    last = i;
                    cto++;
                    continue;
                }
                ans += cto;
                cto++;
                last = i;
            }
        }
        if(last != n-1){
            ans += cto;
        }
        return ans;
    }
};
