class Solution {
public:
    int maxOperations(string s) {
        int aa= 0;
        int bb = 0;
        int ans =0;
        for(int i=0;i<s.length();i++)
        {
            if(s[i]=='1'&&bb>0)
            {
                ans+=aa;
                bb=0;
            }
            if(s[i]=='0')
            {
                bb++;
            }
            if(s[i]=='1')
            {
                aa++;
            }
        }
        if(bb>0)
        {
            ans +=aa;
        }
        return ans;
    }
};
