class Solution {
public:
    int maxOperations(string s) {
        int operationCount = 0;
        int consecutiveZeroes = 1; // Buffer to track consecutive '0's to the right
        for (int i = s.size() - 2; i >= 0; i--) {
            if (s[i] == '1' && s[i + 1] == '0') {
                operationCount += consecutiveZeroes;
                consecutiveZeroes++;
            } else if (s[i] == '1' && s[i + 1] == '1' && operationCount > 0) {
                operationCount += consecutiveZeroes - 1;
            }
        }
        return operationCount;
    }
};
