class Solution:
    def maxOperations(self, s: str) -> int:
        a = []

        n = len(s)
        for i in range(n):
            if s[i] == '1':
                a.append(i)

        cur = 1
        res = 0

        for i in range(len(a)):
            x = None
            if (i + 1) < len(a):
                x = a[i + 1] - a[i] - 1
            else:
                x = len(s) - a[i] - 1

            if x != 0:
                res += cur

            cur += 1
        return res
