class Solution:
    def maxOperations(self, s: str) -> int:
        zero = 0
        one = 0
        res = 0
        for i in s:
            if i == '0':
                zero += 1
            else:
                if zero:
                    res += one
                zero = 0
                one += 1
        if zero:
            res += one
        return res
