class Solution {
    public int maxOperations(String s) {
        int n = s.length();
        int ans = 0;
        int count = 0;
        for(int i = n - 2; i >= 0; i--) {
            if(s.charAt(i) == '1' && s.charAt(i + 1) == '0')
                count++;
            if(s.charAt(i) == '1')
                ans += count;
        }
        return ans;
    }
}
