class Solution {
public:
    int maxOperations(string s) {
        int ans=0,oa=0;
        bool check=true;
        for(int i=0;i<s.size();i++){
            if(s[i]=='1'){
                oa++;
                check=true;
            }
            else {
                if(check)ans+=oa;
                check=false;
            }
        }
        return ans;
    }
};
