class Solution:
    def maxOperations(self, s: str) -> int:
        ans = 0
        cnt1 = 0
        meet1 = False
        for c in s:
            if c == '1':
                meet1 = True
                cnt1 += 1
            else:
                if meet1 == True:
                    ans += cnt1                
                meet1 = False
        return ans
