class Solution {
public:
    int maxOperations(string s) {
        int res = 0;
        int c1 = 0;
        int c0 = 0;
        for (char c : s) {
            if (c == '1') {
                c1++;
                c0 = 1;
            } else {
                res += c0 * c1;
                c0 = 0;
            }
        }
        return res;
    }
};
