class Solution {
    public int maxOperations(String s) {
        byte[] b = s.getBytes();
        int res = 0, i = 0;
        for (; i < b.length; i++) {
            if (b[i] == '1') {
                break;
            }
        }
        int count = 0;
        for (; i < b.length; i++) {
            if (b[i] == '1') {
                count++;
            } else {
                while (i < b.length && b[i] == '0') {
                    i++;
                }
                i--;
                res += count;
            }
        }
        return res;
    }
}
