class Solution {
public:
    int maxOperations(string s) {
        int n = s.size();

        while (n && s[n - 1] == '1') n -- ;

        int num = 0, pre = 0;
        for (int i = 0; i < n; i ++ ) {
            int j = i;
            while (j < n && s[j] == '1') {
                j ++ ;
            }

            int now = 0;
            if (s[i] == '1') {
                now = j - i;
                i = j - 1;
                pre += now;
                num += pre;
            }
        }

        return num;
    }
};
