class Solution {
public:
    int maxOperations(string s) {
        int n = s.size();
        int ans = 0;
        int ones = 0;

        for (int i=0; i<n; i++){
            if (s[i] == '1'){
                ++ones;
            }
            else if ((i+1 == n) || s[i+1] != '0' ){
                ans += ones;
            }
        }
        return ans;
    }
};
