class Solution {
public:
    int maxOperations(string s) {
        int end = 0;
        for(int i = 0; i < s.length(); ++i){
            if(s[i] == '1'){
                end = i;
            }
        }

        int count = 0;
        int used_ones = 0;
        int unused_ones = 0;
        for(int i = 0; i <= end; ++i){
            unused_ones += (s[i] == '1');
            if(s[i] == '0' && unused_ones != 0){
                count += (used_ones + unused_ones);
                used_ones += unused_ones;
                unused_ones = 0;
            }
        }

        if(end != s.length() - 1){
            count += (used_ones + unused_ones);
        }

        return count;
    }
};
