class Solution {
public:
    int maxOperations(string s) {
        int ans=0,oneCnt=0;
        bool check=true;
        for(int i=0;i<s.size();i++){
            if(s[i]=='1'){
                oneCnt++;
                check=true;
            }
            else{
                if(check)ans+=oneCnt;
                check=false;
            }
        }
        return ans;
    }
};
