class Solution {
public:
    int maxOperations(string s) {
        int u=0;
        int ans=0;
        for(int i=0;i<s.size();){
            if(s[i]=='1'){
                u++;
                i++;
            }else{
                while(i<s.size()&&s[i]=='0')i++;
                ans+=u;
            }
        }
        return ans;
    }
};
