class Solution {
public:
   int f(string s)
   {
     stack<char>st;
        int count=0;
        for(int i=0;i<s.size();i++)
        {
            if(s[i]=='1')
            {
                st.push(1);
            }
            else{
                if(st.size())
                {
                    count+=st.size();
                }
            }
        }
        return count;
   }
    int maxOperations(string s) {
       stack<char>p;
       for(int i=0;i<s.size();i++)
       {
        if(p.size()&&p.top()=='0'&&s[i]=='0')
        {
            continue;
        }
        else{
            p.push(s[i]);
        }
       }
       string st;
       while(!p.empty())
       {
        st.push_back(p.top());
        p.pop();
       }
       reverse(st.begin(),st.end());
       return f(st);
    }
};
