class Solution {
public:
    int maxOperations(string s) {
        int ans=0,store=0,count=0;
        for(int i=0;i<s.size();i++){
            if(s[i]=='1'){
                store++;
            }
            else{
                if(store){
                    ans=ans+count+store;
                    count+=store;
                    store=0;

                }
            }
        }
        return ans;
    }
};
