class Solution {
public:
    int maxOperations(string s) {

        long maxop = 0;

        int ones = 0;
        for(int i = 0 ; i < s.length(); i++){
            if(s[i] == '1') {
                if(i > 0 && s[i - 1] == '0'){
                    maxop += ones;
                }
                ones++;
            }else {
                if(i == s.length() - 1){
                    maxop += ones;
                }
            }
        }
        return (int)maxop;



    }
};
