class Solution:
    def maxOperations(self, s: str) -> int:
        res, ones = 0, 0

        for idx in range(len(s) - 1):
            if s[idx] == "1":
                ones += 1

            if (s[idx] == "0" and s[idx+1] == "1") or (s[idx+1]=="0" and idx+1==len(s)-1):
                res += ones

        return res
