class Solution {
public:
    int maxOperations(string s) {
        int n=s.size(),ans=0;
        string str="";
        for(int i=0;i<n;i++)
        {
            if(i==0)str.push_back(s[i]);
            else {
                if(s[i]==s[i-1] && s[i]=='0')continue;
                str.push_back(s[i]);
            }
        }

        n=str.size();
        int cnt=0;
        for(int i=n-1;i>=0;i--)
        {
            if(str[i]=='0')cnt++;
            else ans+=cnt;
        }
        return ans;
    }
};
