class Solution {
    public int maxOperations(String s) {
        int n = 0;
        int a = 0;

        boolean x = false;

        for (char ch : s.toCharArray()) {
            if (ch == '1') {
                ++n;
                x = true;
            } else if (x){
                a += n;
                x = false;
            }
        }

        return a;
    }
}
