class Solution {
public:
    int maxOperations(string s) {

        int n = s.size();

        int res = 0, cnt = 0;
        for (int i = 0; i < n; ++i) {
            if (s[i] == '0' && (i < n && s[i + 1] == '1' || i + 1 == n)) {
                res += cnt;
            }
            if (s[i] == '1')
                ++cnt;
        }

        return res;
    }
};
