class Solution {
public:
    //没有样例我可能就废了，最快的移动方式从后往前一步到位，最慢的方式从前往后一点一点多次，这样就要数后面有多少零段，零段数量意味着移动次数
    int maxOperations(string s) {
        //字符串存在，组成合法且简单合理
        int ans = 0;
        int cnt1 = 0;
        int cnt_segment = 0;
        int len = s.size();
        len--;
        bool flag = false;
        while(len >= 0)
        {
            while(len >= 0 && s[len] == '0')
            {
                flag = true;
                len--;
            }
            cnt_segment += flag;
            flag = false; 
            while(len >= 0 && s[len] == '1')
            {
                cnt1++;
                len--;
            }
            ans += cnt_segment * cnt1;
            cnt1 = 0;
        }
        return ans;
    }
};
