class Solution {
public:
    int maxOperations(string s) {
        int res = 0, on = 0;
        bool chk = true;
        int i = 0;

        while (i < s.size()) {
            if (s[i] == '1') {
                on++;
                chk = true;
            } else {
                if (chk) res += on;
                chk = false;
            }
            i++;
        }

        return res;
    }
};
