class Solution {
public:
    int maxOperations(string s) {

        int ans =0;
        int last =0;
        int n = s.size();
        if(n==1)
        {
            return 0;
        }
        int i = n-1;
        while(i>=0 and s[i]=='1')
        {
            i--;
        }
        i--;


        for( ;i>=0;i--)
        {

           if(s[i]=='1' and s[i+1]=='0')
           {
               last++;
               ans += last;

           }
            else if(s[i]=='1' and s[i+1]=='1')
            {
                ans+= last;
            }
        }
        return ans;
    }
};
