class Solution:
    def maxOperations(self, s: str) -> int:
        # 1 00 1 00 1
        # 111 0 11 0 1
        #  11111 0 1

        # trim ending 1s
        last_zero = len(s) - 1
        while last_zero >= 0:
            if s[last_zero] == "0":
                break
            last_zero -= 1


        # we only care about the string up until the last zero! all ones after that dont matter.

        # no zeros or only one zero in the very beginning => no moves possible!!
        if last_zero == -1 or last_zero == 0:
            return 0

        total = 0
        cur_ones = 0
        i = 0
        while i <= last_zero:
            if s[i] == "1":
                cur_ones += 1
                i += 1

            # encountered a 0! 
            elif s[i] == "0":
                total += cur_ones
                # skip all following zeros => they dont add anything
                while i <= last_zero and s[i] == "0":
                    i += 1

        return total
