class Solution {
    public int maxOperations(String s) {
        char[] arr = s.toCharArray();
        int ones = 0;
        boolean hasZero = false;
        int tot = 0;
        for(int i=0; i<arr.length; i++) {
            if(arr[i]=='1') {
                if(hasZero) tot += ones;
                hasZero = false;
                ones++;
            } else {
                hasZero = true;
            }
        }
        if(hasZero) {
            tot += ones;
        }
        return tot;
    }
}
