class Solution:
    def maxOperations(self, s: str) -> int:
        ans = 0
        num_of_ones = 0
        for i, c in enumerate(s):
            if c == '1':
                num_of_ones += 1
            else:
                if i > 0 and s[i - 1] != c:
                    ans += num_of_ones
        return ans
