class Solution {
    public int maxOperations(String s) {
        int n = s.length();
        int zeros = 0;
        int prev = 0;
        int ans = 0;
        for(int i = n-1;i>=0;i--){
            char c = s.charAt(i);
            if(c=='0') zeros++;
            else{
                if(zeros>0){
                    int res = prev + 1;
                    prev = res;
                    ans += res;
                    zeros = 0;
                }
                else{
                    int res = prev;
                    prev = res;
                    ans += res;
                }
            }
        }

        return ans;
    }
}
