class Solution {
public:
    int maxOperations(string s) {
        int n=s.length();
        int ops=0;
        int overallcnt=0;
        for(int i=0;i<n;i++){
            int j=i;
            int cnt=0;
            if(s[i]=='1'){
                while(j<n && s[j]=='1'){
                 cnt++;
                 j++;
                }
                if(j<n && s[j]=='0'){
                    overallcnt+=cnt;
                    ops+=overallcnt;
                }
                i=j-1;
            }
            else continue;
        }
        return ops;
    }
};
