#include <string>

class Solution {
public:
    int maxOperations(const std::string& s) {
        return calculateMaxOperations(s);
    }

private:
    int calculateMaxOperations(const std::string& s) {
        int ans = 0;
        int countOnes = 0;
        bool hasOne = false;

        for (char c : s) {
            if (c == '1') {
                countOnes++;  
                hasOne = true;
            } else if (c == '0' && hasOne) {
                ans += countOnes;  
                hasOne = false;    
            }
        }

        return ans;
    }

};
