class Solution {
public:
    int maxOperations(string s) {
        int cnt=0;
        int n=s.size();
        vector<int>res(n,0);
        if(s[0]=='1') {res[0]=1;}
        for(int i=1;i<s.size();i++){
           if(s[i]=='1'){
             res[i]=1+res[i-1];
           }
           else{
            res[i]=res[i-1];
           }
        }
       bool flag=false;
        for(int i=0;i<n;i++){
            if(s[i]=='1'){
                flag=true;
            }
            if(s[i]=='0' && flag){
                flag=false;
                cnt=cnt+res[i];
            }
        }
        return cnt;
    }
};
