class Solution {
public:
    int maxOperations(string s) {
        int ct = 0;
        for(int i = 0; i<s.size(); i++){
            if(s[i]=='1') ct++;
        }
        int ans = 0;
        int f = 0;
        for(int i = s.size()-1; i>=0; i--){
             if(s[i]=='1'){
                if(f==1){
                ans = ans+ct;
                }
                ct--;
                f = 0;
             }
             else{
                f = 1;
             }
        }
        return ans;
    }
};
