class Solution {
public:
    int maxOperations(string s) {
        int n = s.size();
        int ones = 0;
        int index_one = 0;
        for(int i=0;i<n;i++){
            if(s[i]=='1'){
                ones++;
                index_one = i;
                break;
            }
        }

        if(ones==0)  return 0;

        // else
        int ones_count=1;
        int next_one_dist = 0;
        int steps = 0;
        for(int i=index_one+1;i<n;i++){
            if(s[i]=='0'){
                next_one_dist++;
            }
            else{
                if(next_one_dist==0){
                    steps+=0;
                    ones_count++;
                    next_one_dist=0;
                }
                else{

                    steps+=ones_count;
                    ones_count++;
                    next_one_dist=0;
                }
            }
        }

        // final iteration
        if(next_one_dist==0){
                    steps+=0;
                    ones_count++;
                    next_one_dist=0;
                }
                else{

                    steps+=ones_count;
                    ones_count++;
                    next_one_dist=0;
                }


        return steps;
    }
};
