class Solution:
    def maxOperations(self, s: str) -> int:
        ans = 0
        cnt = 0
        for i in range(len(s) - 1):
            if s[i] == '0' and s[i + 1] == '1':
                ans += cnt
            elif s[i] == '1':
                cnt += 1
        else:
            if s[len(s) - 1] == '0':
                ans += cnt
        return ans
