class Solution {
public:
    int maxOperations(string s) {
        int ans=0;
        int c=1;
        int n=s.size();
        for(int i=0;i<n-1;++i){
            if(s[i]=='1' and s[i+1]=='0')ans+=c,c++;
            else if(s[i]=='1' and s[i+1]=='1')c++;
        }
        return ans;
    }
};
