class Solution:
    def maxOperations(self, s: str) -> int:
        ones = 0
        zeros = 0
        ans = 0
        leftOnes = 0
        for char in s:
            if char == "0":
                zeros += 1
            else:
                ones += 1
        for i, char in enumerate(s):
            if char == "0":
                zeros -= 1
            else:
                if zeros == 0: break
                leftOnes += 1
                if i + 1 < len(s) and s[i+1] == "0":
                    ans += leftOnes
        return ans
