class Solution {
public:
    int maxOperations(string s) {
        int ans = 0;
        int i = 0;
        int no_one = 0;

        int flag = 0;

        while (i < s.size()) {
            if(s[i] == '1') {
                no_one++;
                flag = 1;
            }

            else if (s[i] == '0' && flag == 1) {
                ans = ans + no_one;
                flag = 0;

            } 

            i++;
        }

        if(ans == 0)
            return 0;

        return ans;
    }
};
