class Solution {
public:
    int maxOperations(string s) {
        s += '1';
        int n = s.size();
        int num = 0;
        map<int, int> f;
        for (int i = 0; i < n; i ++ ) {
            if (s[i] == '1') {
                if (i == 0 || s[i - 1] == '0') num = i;
                f[num] ++;
            }
        }
        int i = 0, mx = 0;
        for (auto e : f) {
            mx += e.second * (f.size() - i - 1);
            i ++;
        }
        return mx;
    }
};
