class Solution {
    public int maxOperations(String s) {
        int count = 0;
        int ans  = 0;

        boolean k = false;

        for(int i = 0 ; i < s.length() ; i++) {
            char ch = s.charAt(i);
            if( i == s.length() - 1) {
                if(ch == '0' || (ch == '1' && k == true)) ans += count;
                break;
            }

            char j = s.charAt(i+1);

            if(ch == '1' ) {
                if(k == true) {
                    ans += count;
                    k = false;
                }
                if(j == '0') k = true;
                count++;
            }
        }

        return ans;
    }
}
