class Solution {
public:
    int maxOperations(string s) {
        int totalOperations = 0;
        int onesCount = 0;
        bool shouldAdd = true;

        for (int i = 0; i < s.size(); i++) {
            if (s[i] == '1') {
                onesCount++;
                shouldAdd = true;
            } else {
                if (shouldAdd) totalOperations += onesCount;
                shouldAdd = false;
            }
        }

        return totalOperations;
    }
};
