class Solution {
public:
    int maxOperations(string s) {

        while((!s.empty()) && (s.back()=='1')) {
            s.pop_back();
        }

        int ans = 0;
        int n = s.length();

        for( int i=n-1, gc=0; i>=0; i--) {
            if(s[i]=='0') {
                continue;
            }
            gc += 1;
            int j = i;
            while((j>0) && (s[j-1]==s[j])) {
                j--;
            }
            ans += (i-j+1)*gc;
            i=j;
        }

        return ans;
    }
};
