class Solution {
public:
    int maxOperations(string s) {
        int len = s.length();
        int ones = 0;
        int ops = 0;
        for(int i=0;i<len;i++){
            if(s[i]=='0'){
                while(i<len && s[i]=='0'){
                    i++;
                }
                ops=ops+ones;
                i--;
            } else{
                ones++;
            }
        }
        return ops;
    }
};
