class Solution {
public:
    int maxOperations(string s) {
        int done = 0;
        int ans = 0;
        for(int i=0;i<s.length()-1;i++){
           if(s[i]=='1' && s[i+1]=='0'){
               ans = ans+1+done;
               done++;
           }else if(s[i]=='1'){
               done++;
           }
        }
        return ans;
    }
};
