class Solution {
    public int maxOperations(String s) {
        int n = s.length();

        int leftOnes=0;
        int sum=0;
        for(int i=0;i<n-1;i++){
            if(s.charAt(i)=='1'){
                leftOnes++;
            }
            if(s.charAt(i+1)=='0' && s.charAt(i)=='1'){
                sum+=leftOnes;
            }
        }
        return sum;
    }
}
